     function returnNowStr() {
         var today = new Date();
         var dd = today.getDate();
         var mm = today.getMonth() + 1; //January is 0!
         var yyyy = today.getFullYear();

         if (dd < 10) {
             dd = '0' + dd
         }

         if (mm < 10) {
             mm = '0' + mm
         }
         return mm + '/' + dd + '/' + yyyy;
     }
    function checkDateValid(value) {
        var valueField = value;
        if (value == null || value == undefined) {
            valueField = "";
        } else {
            valueField = kendo.toString(value, 'd');
            var comp = valueField.split('/');
            var m = parseInt(comp[0]) > 10 ? parseInt(comp[0]) : "0" + parseInt(comp[0]);
            var d = parseInt(comp[1]) > 10 ? parseInt(comp[1]) : "0" + parseInt(comp[1]);
            var y = parseInt(comp[2]) > 10 ? parseInt(comp[2]) : "0" + parseInt(comp[2]);

            var date = new Date(y, m - 1, d);
            if (date.getFullYear() != y || date.getMonth() + 1 != m || date.getDate() != d) {
                valueField = "";
            } else {
                valueField = m + "/" + d + "/" + y;
            }
        }
        return valueField;

    }

